import React, { Component } from 'react'
import axios from 'axios'
import AdminLayout from '../../components/layouts/AdminLayout'
import authRoute from '../../components/HOC/authRoute'

import {getCookies} from '../../lib/utils'

class AdminMessages extends Component {

    static async getInitialProps({req}) {
        let messagesData
        let reqCooks = getCookies(req)

        try {
            const response = await axios.get(`http://localhost:3000/api/v1/messages`, {
                headers: {
                    'authorization': `Bearer ${reqCooks}`
                }
            })
            messagesData = response.data
        } catch(error) {
            console.log(error)
        }

        return {
            messagesData
        }
    }

    constructor(props) {
        super(props)
        console.log(this.props)
        this.state = {
            messages: this.props.messagesData
        }
    }

    showMessages = (messages) => {
        messages ?
            messages.map((message) => (
                <div className="item" key={message._id}>
                    <div className="delete_btn">
                        X
                    </div>
                    <div className="tag">
                        <span>Name:</span>
                        {message.name}
                    </div>
                    <div className="tag">
                        <span>Email:</span>
                        {message.email}
                    </div>
                    <div className="tag">
                        <span>Message:</span>
                        {message.message}
                    </div>
                </div>
            ))
        :
        null
    }

    render() {
        return(
            <AdminLayout sectionName="Messages">
                <div className="messages_container">
                    {this.showMessages(this.state.messages)}
                </div>
            </AdminLayout>
        )
    }
}

export default authRoute(AdminMessages)
