import React from 'react'
import Link from 'next/link'

const Pizzas = (props) => {

    const showList = () => {
        return props.pizzas.map(pizza => {
            if(pizza.pod !== true) {
                return (
                    <Link 
                    as={`/pizzas/${pizza.idName}`} //masking the url in browser history
                    href={{
                        pathname: `/pizzas`, //this is the actual url interpreted as http://pizzastore.com/pizzas?pizzaId=1
                        query: {
                            pizzaName: pizza.idName 
                        }
                    }}
                    >
                        <a>
                            <div className="item">
                                <div 
                                className="inner-item"
                                style={{
                                    background: `url('/static/images/${pizza.images}')`
                                }}
                                >
                                    <div className="overlay"></div>
                                    <div className="item-nfo">
                                        <h3>{pizza.name}</h3>
                                        <p>
                                            {pizza.shortDesc}
                                        </p>
                                    </div>
                                </div>
                            </div>
                        </a>
                    </Link>
                )
            }
        })
    }

    return(
        <>
            <div className="pizzas_wrapper">
                <div className="articles">
                    <Link 
                    as={`/pizzas/1`} //masking the url in browser history
                    href={{
                        pathname: `/pizzas`, //this is the actual url interpreted as http://pizzastore.com/pizzas?pizzaId=1
                        query: {
                            pizzaId: 1
                        }
                    }}
                    >
                        <a>
                            <div className="item">
                                <div 
                                className="inner-item"
                                style={{
                                    background: `url('/static/images/pizza_3.jpg')`
                                }}
                                >
                                    <div className="overlay"></div>
                                    <div className="item-nfo">
                                        <h3>American BBQ</h3>
                                        <p>
                                        Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.
                                        </p>
                                    </div>
                                </div>
                            </div>
                        </a>
                    </Link>

                    <Link
                     as={`/pizzas/2`} //masking the url in browser history
                     href={{
                         pathname: `/pizzas`, //this is the actual url interpreted as http://pizzastore.com/pizzas?pizzaId=1
                         query: {
                             pizzaId: 2
                         }
                     }}
                    >
                        <a>
                            <div className="item">
                                <div 
                                className="inner-item"
                                style={{
                                    background: `url('/static/images/pizza_4.jpg')`
                                }}
                                >
                                    <div className="overlay"></div>
                                    <div className="item-nfo">
                                        <h3>American BBQ</h3>
                                        <p>
                                        Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.
                                        </p>
                                    </div>
                                </div>
                            </div>
                        </a>
                    </Link>

                    <Link
                     as={`/pizzas/3`} //masking the url in browser history
                     href={{
                         pathname: `/pizzas`, //this is the actual url interpreted as http://pizzastore.com/pizzas?pizzaId=1
                         query: {
                             pizzaId: 3
                         }
                     }}
                    >
                        <a>
                            <div className="item">
                                <div 
                                className="inner-item"
                                style={{
                                    background: `url('/static/images/pizza_5.jpg')`
                                }}
                                >
                                    <div className="overlay"></div>
                                    <div className="item-nfo">
                                        <h3>American BBQ</h3>
                                        <p>
                                        Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.
                                        </p>
                                    </div>
                                </div>
                            </div>
                        </a>
                    </Link>
                </div>
            </div>
        </>
    )
}

export default Pizzas
