import React, { Component } from 'react'

class Pizzas extends Component {

    static async getInitialProps({ query }) {
        let pizzaData
        pizzaData = query.pizzaId
        return {
            pizzaData
        }
    }

    render() {
        return(
            <>
            This pizza id is: {this.props.pizzaData}
            </>
        )
    }
}

export default Pizzas
